import { Component } from 'react';

import logo from './logo.svg';
import './App.css';

class App extends Component {
constructor() {
  super();

  this.state = {
    name: {firstName: 'Steve', lastName: 'Meadows'},
    message: 'how are you?'
  }
}

  render() {
    return (
      <div className="App">
        <header className="App-header">
        <h1>React.JS</h1>
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Hi {this.state.name.firstName} {this.state.name.lastName}, {this.state.message}
          </p>
          <button onClick={() => {
            this.setState((state, props) => {
              return {
                name: {firstName: 'Oliver', lastName: 'Meadows Dema'}
              }
            }, () => {});
          }}
          >Change Name</button>
        </header>
      </div>
    );
  }
}

export default App;
